import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
import { Redirect } from "react-router";
import grubhubLogo from "../images/grubhubLogo.png";

//create the Navbar Component
class Navbar extends Component {

    //handle logout to destroy the cookie
    handleLogout = () => {
        if (cookie.load("customercookie")) {
            cookie.remove("customercookie", { path: "/" });
        }
        else if (cookie.load("ownercookie")) {
            cookie.remove("ownercookie", { path: "/" });
        }
    };

    render() {
        let navLogo = null;
        let navLogin = null;
        let navProfile = null;
        let loginLink = null;
        let newLink = null;
        let userNav = null;

        if (cookie.load("customercookie") || cookie.load("ownercookie")) {
            loginLink = <Link to="/" class="nav-link" onClick={this.handleLogout}>Logout</Link>;
        }
        else {
            loginLink = <Link to="/login" class="nav-link" onClick={this.handleLogout}>Login</Link>;
        }

        navLogo = (
            <ul class="nav navbar-nav">
                <li class="nav-item">
                    <Link to='/' class="nav-link" href="#">
                        <img src={grubhubLogo} width="60" height="auto" class="d-inline-block align-top" alt="Grubhub" />
                    </Link>
                </li>
            </ul>
        );

        navLogin = (
            <ul class="nav navbar-nav navbar-right">
                <li class="nav-item">
                    {loginLink}
                </li>
            </ul>
        );

        navProfile = (
            <ul class="nav navbar-nav navbar-right">
                <li class="nav-item">
                    <Link to="/profile" class="nav-link" href="#">
                        Profile
                    </Link>
                </li>
            </ul>
        )

        if (cookie.load("ownercookie") || cookie.load("usercookie")) {
            navProfile = (
                <div class="collapse navbar-collapse navbar-right" id="navbarNav">

                    {navLogin}
                </div>
            );

            // custom link loop
            if (cookie.load("usercookie")) {
                newLink = (
                    <ul class="nav navbar-nav navbar-right">
                        <li class="nav-item">
                            <Link to="/customer/cart" class="nav-link" href="#">
                                Cart
              </Link>
                        </li>
                    </ul>
                );
            }
            else {
                newLink = (
                    <ul class="nav navbar-nav navbar-right">
                        <li class="nav-item">
                            <Link to="/restaurant/menu" class="nav-link" href="#">
                                Menu
              </Link>
                        </li>
                    </ul>
                );
            }



        }

        return (
            <div>
                <nav class="navbar navbar-expand-lg navbar-dark  bg-dark">
                    {navLogo}
                    {navProfile}
                </nav>
            </div>
        );
    }
}

export default Navbar;
