import React, {Component} from 'react';
import axios from 'axios';

class Login extends Component {
    constructor(props){
        super(props);
        this.state = {
            user: ""
        };
        this.submitLogin = this.submitLogin.bind(this);
    }

    submitLogin(){
        axios.get('http://localhost:3001/customer/4')
        .then(response => {
                console.log("Status Code : ",response.status);
                if(response.status === 200){
                    this.setState({
                        user : response[0].name
                    })
                }
                else{
                    this.setState({
                        user : "No Data"
                    })
                }
        })
        .catch(error => {
            this.setState({
                user : error.response
            })
        });
    }

    render(){
       return (
           <div>
        <div>{this.state.user}</div>
        <button onClick = {this.submitLogin} class="btn btn-primary">Show Users</button>
        </div>
        )
    }
}

export default Login;
