import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
import { Redirect } from "react-router";
import grubhublogo from "../images/grubhub-icon.png";

//create the Navbar Component
class Navbar extends Component {
  constructor(props) {
    super(props);
  }
  //handle logout to destroy the cookie
  handleLogout = () => {
    if (cookie.load("usercookie")) {
      cookie.remove("usercookie", { path: "/" });
    } else {
      cookie.remove("ownercookie", { path: "/" });
    }
  };

  render() {
    //if Cookie is set render Logout Button
    let navLogin = null;
    let newlink = null;
    let homelink = null;
    let profilelink = null;
    let userNav = null;

    //userNav loop
    if (cookie.load("ownercookie") || cookie.load("usercookie")) {
      console.log("Able to read cookie");
      if (cookie.load("usercookie")) {
        homelink = "/customer/home";
        profilelink = "/customer/profile";
      } else {
        homelink = "/restaurant/home";
        profilelink = "/customer/profile";
      }

      //userNav
      userNav = (
        <div class="collapse navbar-collapse navbar-right" id="navbarNav">
          <ul class="nav navbar-nav">
            <li class="nav-item">
              <Link to={homelink} class="nav-link" href="#">
                <img
                  src={grubhublogo}
                  width="60"
                  height="auto"
                  class="d-inline-block align-top"
                  alt=""
                />
              </Link>
            </li>
          </ul>
          <ul class="nav navbar-nav navbar-right">
            <li class="nav-item">
              <Link to={profilelink} class="nav-link" href="#">
                Profile
              </Link>
            </li>
          </ul>
        </div>
      );

      // custom link loop
      if (cookie.load("usercookie")) {
        newlink = (
          <ul class="nav navbar-nav navbar-right">
            <li class="nav-item">
              <Link to="/customer/cart" class="nav-link" href="#">
                Cart
              </Link>
            </li>
          </ul>
        );
      } else {
        newlink = (
          <ul class="nav navbar-nav navbar-right">
            <li class="nav-item">
              <Link to="/restaurant/menu" class="nav-link" href="#">
                Menu
              </Link>
            </li>
          </ul>
        );
      }
      //logout button loop
      navLogin = (
        <ul class="nav navbar-nav navbar-right">
          <li class="nav-item">
            <Link to="/" class="nav-link" onClick={this.handleLogout}>
              Logout
            </Link>
          </li>
        </ul>
      );
    } else {
      userNav = (
        <div class="collapse navbar-collapse navbar-right" id="navbarNav">
          <ul class="nav navbar-nav">
            <li class="nav-item">
              <Link to={homelink} class="nav-link" href="#">
                <img
                  src={grubhublogo}
                  width="60"
                  height="auto"
                  class="d-inline-block align-top"
                  alt=""
                />
              </Link>
            </li>
          </ul>
        </div>
      );
      //Else display login button
      console.log("Not Able to read cookie");
      navLogin = (
        <ul class="nav navbar-nav navbar-right">
          <li class="nav-item">
            <Link to="/login" class="nav-link" onClick={this.handleLogout}>
              Login
            </Link>
          </li>
        </ul>
      );
    }

    //if Cookie is set render Logout Button
    let redirectVar = null;
    //redirect based on successful login
    if (cookie.load("ownercookie")) {
      redirectVar = <Redirect to="/restaurant/home" />;
    } else if (cookie.load("usercookie")) {
      redirectVar = <Redirect to="/customer/home" />;
    }

    return (
      <div>
        {redirectVar}
        <nav class="navbar navbar-expand-lg navbar-dark  bg-dark">
          {userNav}
          {newlink}
          {navLogin}
        </nav>
      </div>
    );
  }
}

export default Navbar;
