import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import grubhubLoginImage from '../images/GrubhubLoginImage.png'

class OwnerSignup extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            name: "",
            email_id: "",
            password: "",
            address: "",
            phone_number: "",
            res_name: "",
            res_cuisine: "",
            res_zip_code: "",
            signupFlag: false
        }
        //Bind the handlers to this class
        //this.onChange = this.onChange.bind(this);
        //this.onSubmit = this.onSubmit.bind(this);
    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    //submit Login handler to send a request to the node backend
    onSubmit = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            name: this.state.name,
            email_id: this.state.email_id,
            password: this.state.password,
            address: this.state.address,
            phone_number: this.state.phone_number,
            res_name: this.state.res_name,
            res_cuisine: this.state.res_cuisine,
            res_zip_code: this.state.res_zip_code
        }
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/newrestaurant', data)
        .then(response => {
            if (response.status === 200) {
                alert("You have registered your restaurant successfully")
                this.setState({
                    signupFlag: true
                })
            }
        })
        .catch(error => {
            this.setState({
                message: error.response.data
            })
        });
    }

    render() {
        //redirect based on successful signup
        let redirectVar = null;
        if (cookie.load('ownercookie')) {
            redirectVar = <Redirect to="/OwnerHome" />
        }
        else if(this.state.signupFlag){
            redirectVar = <Redirect to= "/Login"/>
        }
        return (
            <div>
                {redirectVar}
                <div>
                <img src={grubhubLoginImage} style={{height: 'fit-content'}} alt='GrubHub' />
                </div>
                <div class="container">

                    <div class="login-form">
                        <div class="main-div">
                            <div class="panel">
                                <h2>Signup for new Restaurant Account</h2>
                            </div>
                            <form onSubmit={this.onSubmit}>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="name" placeholder="Name" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="res_name" placeholder="Restaurant Name" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="res_cuisine" placeholder="Cuisine" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="email_id" placeholder="Email Id" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="password" class="form-control" required name="password" placeholder="Password" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="res_zip_code" placeholder="ZIP Code" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="address" placeholder="Address" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="phone_number" placeholder="Phone Number" />
                                </div>
                                <div style={{ color: "#ff0000" }}>{this.state.message}</div>
                                <button type="submit" class="btn btn-primary">Signin</button><br/><br/>
                                <div>Already have an account? <Link to='/login'>Login</Link></div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
//export Login Component
export default OwnerSignup;
