import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
import { Redirect } from "react-router";
import grubhubLogo from "../images/grubhubLogo.png";

//create the Navbar Component
class Navbar extends Component {

    //handle logout to destroy the cookie
    handleLogout = () => {
        if (cookie.load("customercookie")) {
            cookie.remove("customercookie", { path: "/" });
        }
        else if (cookie.load("ownercookie")) {
            cookie.remove("ownercookie", { path: "/" });
        }
    };

    render() {
        let navLogin = null;
        let newLink = null;
        let userNav = null;
        let navLogo = null;

        navLogo = (
            <ul class="nav navbar-nav">
                <li class="nav-item">
                    <Link to='/home' class="nav-link" href="#">
                        <img src={grubhubLogo} width="60" height="auto" class="d-inline-block align-top" alt="Grubhub" />
                    </Link>
                </li>
            </ul>
        );


        //userNav
        if (cookie.load("ownercookie") || cookie.load("usercookie")) {
            userNav = (
                <div class="collapse navbar-collapse navbar-right" id="navbarNav">
                    {navLogo}
                    <ul class="nav navbar-nav navbar-right">
                        <li class="nav-item">
                            <Link to='/profile' class="nav-link" href="#">
                                Profile
                        </Link>
                        </li>
                    </ul>
                </div>
            );

            // custom link loop
            if (cookie.load("usercookie")) {
                newLink = (
                    <ul class="nav navbar-nav navbar-right">
                        <li class="nav-item">
                            <Link to="/customer/cart" class="nav-link" href="#">
                                Cart
              </Link>
                        </li>
                    </ul>
                );
            }
            else {
                newLink = (
                    <ul class="nav navbar-nav navbar-right">
                        <li class="nav-item">
                            <Link to="/restaurant/menu" class="nav-link" href="#">
                                Menu
              </Link>
                        </li>
                    </ul>
                );
            }
            //logout button loop
            navLogin = (
                <ul class="nav navbar-nav navbar-right">
                    <li class="nav-item">
                        <Link to="/" class="nav-link" onClick={this.handleLogout}>
                            Logout
            </Link>
                    </li>
                </ul>
            );
        }
        else {
            userNav = (
                <div class="collapse navbar-collapse navbar-right" id="navbarNav">
                    {navLogo}
                </div>
            );

            navLogin = (
                <ul class="nav navbar-nav navbar-right">
                    <li class="nav-item">
                        <Link to="/login" class="nav-link" onClick={this.handleLogout}>
                            Login
            </Link>
                    </li>
                </ul>
            );
        }

        //if Cookie is set render Logout Button
        let redirectVar = null;
        //redirect based on successful login
        if (cookie.load("ownercookie")) {
            redirectVar = <Redirect to="/restaurant/home" />;
        } else if (cookie.load("usercookie")) {
            redirectVar = <Redirect to="/customer/home" />;
        }

        return (
            <div>
                {redirectVar}
                <nav class="navbar navbar-expand-lg navbar-dark  bg-dark">
                    {userNav}
                    {newLink}
                    {navLogin}
                </nav>
            </div>
        );
    }
}

export default Navbar;
