import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
import { Redirect } from "react-router";
import grubhubLogo from "../images/grubhubLogo.png";

//create the Navbar Component
class Navbar extends Component {
  //handle logout to destroy the cookie
  handleLogout = () => {
    cookie.remove("ownercookie", { path: "/" });
    cookie.remove("customercookie", { path: "/" });
  };

  render() {
    //if Cookie is set render Logout Button
    let redirectVar = null;
    if (cookie.load("cookie")) {
      redirectVar = <Redirect to="/" />;
    }
    return (
      <div>
        {redirectVar}
        <nav class="navbar navbar-expand-lg navbar-dark  bg-dark">
          <a class="navbar-brand" href="#">
            <img
              src={grubhubLogo}
              width="60"
              height="auto"
              class="d-inline-block align-top"
              alt=""
            />
          </a>

          <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="nav navbar-nav navbar-right">
              <li class="nav-item active">
                <Link class="nav-link" href="#">
                  Home <span class="sr-only">(current)</span>
                </Link>
              </li>
              <li class="nav-item">
                <Link to="/customer/profile" class="nav-link" href="#">
                  Profile
                </Link>
              </li>
            </ul>
          </div>

          <ul class="nav navbar-nav navbar-right">
            <li class="nav-item">
              <Link to="/" class="nav-link" onClick={this.handleLogout}>
                Logout
              </Link>
            </li>
          </ul>
        </nav>
      </div>
    );
  }
}

export default Navbar;
