import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import { Redirect } from 'react-router';
import homeImage from '../images/homeImage.png'
const imageWidth = {
    height: 'fit-content'
};

class CustomerSignup extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            username: "",
            password: "",
            authFlag: false,
            errorMessage: ""
        }
        //Bind the handlers to this class
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    //Call the Will Mount to set the auth Flag to false
    componentWillMount() {
        this.setState({
            authFlag: false
        })
    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    //submit Login handler to send a request to the node backend
    onSubmit = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            name: this.state.name,
            email_id: this.state.email_id,
            password: this.state.password,
            address: this.state.address,
            phone_number: this.state.phone_number
        }
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/login', data)
            .then(response => {
                console.log("Status Code : ", response.status);
                if (response.status === 200) {
                    this.setState({
                        authFlag: true
                    })
                }
                else {
                    this.setState({
                        authFlag: false
                    })
                }
            })
            .catch(error => {
                this.setState({
                    authFlag: false,
                    message: error.response.data
                })
            });

    }

    render() {
        //redirect based on successful login
        let redirectVar = null;
        return (
            <div>
                {redirectVar}
                <div>
                <img src={homeImage} style={imageWidth} alt='GrubHub' />
                </div>

                    <div class="login-form">
                        <div class="main-div">
                            <div class="panel">
                                <h2>Signup for Grubhub account</h2>
                            </div>
                            <form onSubmit={this.onSubmit}>
                                <div style={{ color: "#ff0000" }}>{this.state.message}</div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="name" placeholder="Name" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="email_id" placeholder="Email Id" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="password" class="form-control" required name="password" placeholder="Password" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="address" placeholder="Address" />
                                </div>
                                <div class="form-group">
                                    <input onChange={this.onChange} type="text" class="form-control" required name="phone_number" placeholder="Phone Number" />
                                </div>
                                <button type="submit" class="btn btn-primary">Signup</button>
                            </form>
                        </div>
                    </div>
            </div>
        )
    }
}
//export Login Component
export default CustomerSignup;
