import React, { Component } from 'react';
import axios from 'axios';
import cookie from 'react-cookies';
import { Link } from "react-router-dom";

class CustomerProfile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user_id: "",
            name: "",
            email_id: "",
            password: "",
            user_image: ""
        };
    }

    componentWillMount() {
        const data = {
            user_id: localStorage.getItem("user_id")
        };

        axios.post("http://localhost:3001/userget", data)
            .then(response => {
                this.setState({
                    user_id: response.data.user_id,
                    name: response.data.name,
                    email_id: response.data.email_id,
                    password: response.data.password,
                    user_image: response.data.user_image
                });
            })
            .catch(error => {
                console.log(error);
            });
    }

    onUpdate = () => {
        let data = {
            user_id: localStorage.getItem("user_id")
        };
        axios.post("http://localhost:3001/customerupdate", data)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            });
    };

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        return (
            <div>
                <h2>Profile</h2>
                <form onSubmit={this.handleUpdate}>
                    <div className="row">
                        <div className="form-group col-sm-6">
                            <label htmlFor="email">Email</label>
                            <input
                                type="text"
                                className="form-control"
                                name="email"
                                id="email"
                                value={buyer.username}
                                disabled
                            />
                        </div>
                        <div className="form-group col-sm-6">
                            <label htmlFor="name">Name</label>
                            <input
                                type="text"
                                className="form-control"
                                name="name"
                                id="name"
                                onChange={this.handleChange}
                                value={buyer.name}
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="form-group col-sm-12">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                className="form-control"
                                name="email"
                                id="email"
                                onChange={this.handleChange}
                                value={buyer.email}
                                disabled
                            />
                        </div>
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            className="form-control"
                            name="password"
                            id="password"
                            onChange={this.handleChange}
                            value={buyer.password}
                        />
                    </div>
                    <div className="row">
                        <button type="submit" className="col-sm-12 btn btn-primary">
                            Update
            </button>
                        <Link to="/home">
                            <button className="col-sm-12 btn btn-primary">cancel</button>
                        </Link>
                    </div>
                </form>
            </div>
        )
    }
}
export default CustomerProfile;

