import React, { Component } from 'react';
import axios from 'axios';
import cookie from 'react-cookies';
import { Link } from "react-router-dom";
import ImageUploader from "../ImageUploader"

class CustomerProfile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user_id: "",
            name: "",
            email_id: "",
            password: "",
            user_image: ""
        };
        this.onChange = this.onChange.bind(this);
        this.onUpdate = this.onUpdate.bind(this);
    }

    componentWillMount() {
        const data = {
            user_id: localStorage.getItem("user_id")
        };

        axios.post("http://localhost:3001/userget", data)
            .then(response => {
                this.setState({
                    user_id: response.data[0].user_id,
                    name: response.data[0].name,
                    email_id: response.data[0].email_id,
                    user_image: response.data[0].user_image,
                    address: response.data[0].address,
                    phone_number: response.data[0].phone_number
                });
            })
            .catch(error => {
                console.log(error);
            });
    }

    onUpdate = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        let data = {
            user_id: localStorage.getItem("user_id"),
            email_id: this.state.email_id,
            name: this.state.name,
            password: this.state.password,
            address: this.state.address,
            phone_number: this.state.phone_number
        };
        axios.post("http://localhost:3001/customerupdate", data)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            });
    };

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        return (
            <div>
                <h2>Customer Profile</h2>
                <form onSubmit={this.onUpdate}>
                        <div className="form-group">
                            <label htmlFor="name">Name</label>
                            <input
                                type="text"
                                className="form-control"
                                name="name"
                                onChange={this.onChange}
                                value={this.state.name}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                className="form-control"
                                name="email_id"
                                value={this.state.email_id}
                                disabled
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Change Password</label>
                            <input
                                type="password"
                                className="form-control"
                                name="password"
                                onChange={this.onChange}
                                placeholder = "New Password"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="address">Address</label>
                            <input
                                type="text"
                                className="form-control"
                                name="address"
                                onChange={this.onChange}
                                value={this.state.address}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="phone_number">Phone Number</label>
                            <input
                                type="text"
                                className="form-control"
                                name="phone_number"
                                onChange={this.onChange}
                                value={this.state.phone_number}
                            />
                        </div>
                        <button type="submit" className="col-sm-12 btn btn-primary">
                            Update
                        </button>
                        <Link to="/home">
                            <button className="col-sm-12 btn btn-primary">cancel</button>
                        </Link>

                </form>
            </div>
        )
    }
}
export default CustomerProfile;
